mapper.select:
SELECT CATEGORY, SEARCHNAME, PRICE FROM ACSEARCH
WHERE SEARCHNAME like '%' || #{SEARCHNAME} || '%' or category = #{SEARCHNAME}
ORDER BY SEARCHNAME ASC

SELECT CATEGORY, SEARCHNAME, PRICE FROM ACSEARCH
WHERE SEARCHNAME like '%' || #{SEARCHNAME} || '%' or category = #{SEARCHNAME}
ORDER BY price DESC

SELECT CATEGORY, SEARCHNAME, PRICE FROM ACSEARCH
WHERE SEARCHNAME like '%' || #{SEARCHNAME} || '%' or category = #{SEARCHNAME}
ORDER BY price ASC
